import React from 'react';
import ReactDOM from 'react-dom';
import './Home.css';


class Home extends React.Component{
  state={
    inputValue: ''
  };

  onClick = (value) => {
    this.setState({ 
      inputValue: this.state.inputValue + value })
  }

  delNum=()=>{
    this.setState({inputValue: ""})
  }

  render(){
    return (
        <section className="container">
            <div className = "calculator">
                <input className="input" type="text" value={this.state.inputValue}/>
            </div>
            <div className = "low">
                <button className="button" onClick={this.delNum}>AC</button>
                <button className="button" onClick={()=>this.onClick("%")}>%</button>
            </div>
            <div className = "low">
                <button className="button" onClick={()=>this.onClick(7)}>7</button>
                <button className="button" onClick={()=>this.onClick(8)}>8</button>
                <button className="button" onClick={()=>this.onClick(9)}>9</button>
            </div>
        </section>
    );
  }

}


export default Home;
